class EntryConnection {
  static Future<Entry> getEntryFromWikititle({
    required BuildContext context,
    required String wikititle,
    String? redirectFrom,
    required isOnline,
  }) async {
    late Entry output;

    if (kIsWeb) {
      final response = await html.HttpRequest.postFormData(
        SourceWiktionary.fromSettings().url,
        {
          'origin': '*', //for CORS stuff
          'action': 'parse',
          'format': 'json',
          'formatversion': '2',
          'page': wikititle,
          'prop': 'text|revid|displaytitle|limitreportdata|properties',
          'disableeditsection': '1',
          'disabletoc': '1',
          'curtimestamp': '1',
        },
      );
      output = Entry.fromJson(
        context,
        jsonDecode(response.response),
        redirectFrom: redirectFrom,
      );
    } else if (isOnline) {
      final response = await http.post(
        Uri.parse(SourceWiktionary.fromSettings().url),
        body: {
          'origin': '*', //for CORS stuff
          'action': 'parse',
          'format': 'json',
          'formatversion': '2',
          'page': wikititle,
          'prop': 'text|revid|displaytitle|limitreportdata|properties',
          'disableeditsection': '1',
          'disabletoc': '1',
          'curtimestamp': '1',
        },
      );

      output = Entry.fromJson(
        context,
        jsonDecode(response.body),
        redirectFrom: redirectFrom,
      );
    } else {
      throw UnimplementedError();
    }

    return output;
  }

  static Future<List<EntryLink?>> getEntryLinksFromWikititle({
    required String wikititle,
    required bool isOnline,
    required int namespaceId,
    int offset = 0,
  }) async {
    late List<EntryLink?> output;

    if (kIsWeb) {
      final response = await html.HttpRequest.postFormData(
        SourceWiktionary.fromSettings().url,
        {
          'origin': '*', //for CORS stuff
          'action': 'query',
          'format': 'json',
          'formatversion': '2',
          'gpssearch': wikititle,
          'gpsoffset': offset.toString(),
          'gpsnamespace': namespaceId.toString(),
          'generator': 'prefixsearch',
        },
      );
      output = EntryLink.fromJson(
        jsonDecode(response.response),
      );
    } else if (isOnline) {
      var response = await http.post(
        Uri.parse(SourceWiktionary.fromSettings().url),
        body: {
          'origin': '*', //for CORS stuff
          'action': 'query',
          'format': 'json',
          'formatversion': '2',
          'gpssearch': wikititle,
          'gpsoffset': offset.toString(),
          'gpsnamespace': namespaceId.toString(),
          'generator': 'prefixsearch',
        },
      );

      output = EntryLink.fromJson(
        jsonDecode(response.body),
      );
    } else {
      throw UnimplementedError();
    }

    return output;
  }

// no `isOnline` as cannot search title from online
  static Future<Entry> getEntryFromTitle({
    String title = '',
    String? redirectFrom,
  }) async {
    throw UnimplementedError();
  }
}

